mapper.select:
select p.id, p.title, u.nickname, c.name catergory_name, p.reg_date, p.view, ifnull( sum( if(pv.vote_type = 'up', 1, null) ), 0 ) - ifnull( sum( if(pv.vote_type = 'down', 1, null) ), 0 ) as votes from posts p join users u on p.user_id = u.id join categories c on p.category_id = c.id left outer join post_votes pv on p.id= pv.post_id
p.board_id=#{boardId}
and p.content like concat('%', #{q}, '%')
group by p.id ORDER BY p.reg_date DESC LIMIT #{offset}, #{size};

select count(*) from posts p join users u on p.user_id = u.id
p.board_id=#{boardId}
and p.content like concat('%', #{q}, '%')
